                    title: "contract.mvir",
                    code:"modules: \n//在这里输入代码\n\nmodule BuildCastle {\n\n}\n\nscript: \nimport Transaction.BuildCastle;\n//在这里输入代码\n\nmain() {\n    let SerialDigits: u64;\n    //在这里输入代码\n\n    SerialDigits = 8;\n    return;\n}",
